if(!defined('InXingAo')){exit('No InXingAo');}

//DHL
function DHL($rs)
{
	global $xingao,$LG,$ret_logistics;
	global $dhl_username,$dhl_password,$dhl_PortalId,$dhl_DeliveryName;
	
	
	//这些字段必填,否则无法获取
	if($rs['f_name']&&$rs['f_add_chengshi']&&$rs['f_zip']&&$rs['f_add_quzhen']&&$rs['f_add_dizhi'])
	{
		require_once($_SERVER['DOCUMENT_ROOT'].'/api/logistics/dhl.php');//获取页
		$dhl_service = new DHLRequest($dhl_wsdlPath,$dhl_callOptions,cadd($dhl_username),cadd($dhl_password));
		
		//获取DHL数据-开始----------------
		$dhl_send = new BookLabelRequestType();
		$dhl_send->portalId =cadd($dhl_PortalId);
		$dhl_send->deliveryName =cadd($dhl_DeliveryName);//生成的PDF的收件信息从此获取 (地址信息在官方平台设置)
		$dhl_send->labelFormat = 'PDF';//label生成的格式
		
		//测试:如果失败过一会再试:可能官方需要验证IP
/*		$rs['f_name']='Alfred';
		$rs['f_add_chengshi']='Testhausen';
		$rs['f_mobile']='02251 12342';
		$rs['f_zip']='55555';
		$rs['f_add_quzhen']='Teststr';
		$rs['f_add_dizhi']='123';
*/		
		//设置-开始
		$dhl_send->customerReference =cadd($rs['ydh']);//本站运单号
		$dhl_send->senderName1 =cadd($rs['f_name']);//发件人-人名1
		$dhl_send->senderName2 = '';//发件人-人名2
		$dhl_send->senderCareOfName = '';//发件人-代理人名
		$dhl_send->senderCity = cadd($rs['f_add_chengshi']);//发件人-城市
		$dhl_send->senderContactPhone = cadd($rs['f_mobile']);//发件-电话
		$dhl_send->senderPostalCode = cadd($rs['f_zip']);//发件-邮编
		$dhl_send->senderStreet = cadd($rs['f_add_quzhen']);//发件-街道
		$dhl_send->senderStreetNumber = cadd($rs['f_add_dizhi']);//发件-街道号码
		$dhl_send->senderBoxNumber = '';//发件-楼层号码
		//设置-结束
		
		//print_r($dhl_send);exit;
		$dhl_data=$dhl_service->BookLabel($dhl_send);//从DHL接口返回XML格式
		//print_r($dhl_data);exit;
		//获取DHL数据-结束----------------
		
		
		//处理数据及保存----------------
		$dhl_data=json_encode($dhl_data, true);//XML转为json
		$dhl_data=json_decode($dhl_data, true);//json转为数组
		if($dhl_data['idc'])
		{
			$dhl_data=json_encode($dhl_data);//数组转转为json
			$xingao->query("update yundan set dhl='".add($dhl_data)."' where ydid='{$rs[ydid]}' ");SQLError('添加dhl');//保存
			$ret_logistics['succeed']++;
		}
	}else{
		$ret_logistics['failure']++;
	}
	
	return $ret_logistics;
	
	
	
